    <form className="barreRecherche" onSubmit={handleSubmit}>
      <div>
        <input
          type="text"
          placeholder="Rechercher sur ReFrame"
          value={recherche}
          onChange={handleInputChange}
        />
        <button type="submit">Rechercher</button>
      </div>
    </form>
