import React, { useState } from 'react';
import SaisieMessage from './SaisieMessage';
import {
  faTrash,
  faComment,
  faHeart as faHeartSolid,
} from '@fortawesome/free-solid-svg-icons';
import { faHeart as faHeartRegularIcon } from '@fortawesome/free-regular-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import axios from 'axios';
export default function Message(props) {
  const [saisir, setSaisir] = useState(false);
  const [isMenuOpen, setMenuOpen] = useState(false);
  const [isLiked, setIsLiked] = useState(false);

  const pageProfilHandler = (evt) => {
    evt.preventDefault();
    props.setCurrentPage(props.login);
  };

  const toggleMenu = () => {
    setMenuOpen(!isMenuOpen);
  };

  const deleteMessage = (evt) => {
    evt.preventDefault();
    axios
      .delete(`/user/${props.id}/messages`, {
        headers: {
          'Content-Type': 'application/json',
        },
        withCredentials: true,
        credentials: 'include',
      })
      .then((res) => {
        console.log(res.data);
        props.setMessages(props.messages.filter((msg) => msg.id !== props.id));
        props.setRechargerMessages(!props.rechargerMessages);
      })
      .catch((err) => {
        console.log(err.response.data);
      });
  };

  const warning = (evt) => {
    evt.preventDefault();
    axios
      .post(
        `/messages/warning/${props.id}`,
        {},
        {
          headers: {
            'Content-Type': 'application/json',
          },
          withCredentials: true,
          credentials: 'include',
        }
      )
      .then((res) => {
        console.log(res.data);
        props.setRechargerMessages(!props.rechargerMessages);
      })
      .catch((err) => {
        console.log(err.response.data);
      });
  };

  const like = (evt) => {
    evt.preventDefault();
    axios
      .post(
        `/messages/like/${props.myLogin}/${props.id}`,
        {},
        {
          headers: {
            'Content-Type': 'application/json',
          },
          withCredentials: true,
          credentials: 'include',
        }
      )
      .then((res) => {
        console.log(res.data);
        setIsLiked(true);
        props.setRechargerMessages(!props.rechargerMessages);
      })
      .catch((err) => {
        console.log(err.response.data);
      });
  };

  const dislike = (evt) => {
    evt.preventDefault();
    axios
      .post(
        `/messages/dislike/${props.myLogin}/${props.id}`,
        {},
        {
          headers: {
            'Content-Type': 'application/json',
          },
          withCredentials: true,
          credentials: 'include',
        }
      )
      .then((res) => {
        console.log(res.data);
        setIsLiked(false);
        props.setRechargerMessages(!props.rechargerMessages);
      })
      .catch((err) => {
        console.log(err.response.data);
      });
  };

  const handlerSaisie = (evt) => {
    if (saisir) setSaisir(false);
    else setSaisir(true);
  };

  const comment = (evt) => {
    evt.preventDefault();
    const data = {
      messageId: props.id,
      date: date,
      clock: clock,
      content: content,
    };
    axios
      .put('/newComment', data, {
        headers: {
          'Content-Type': 'application/json',
        },
        withCredentials: true,
        credentials: 'include',
      })
      .then((res) => {
        console.log(res.data);
        props.setRechargerMessages(!props.rechargerMessages);
      })
      .catch((err) => {
        console.log(err.response.data);
      });
  };

  return (
    <article className="message">
      <div>
        <a className="user" href="a" onClick={pageProfilHandler}>
          {props.login}
        </a>
        <button className="menuBurger" onClick={toggleMenu}>
          ...
        </button>
        {isMenuOpen && (
          <div className="menuList">
            {props.myLogin === props.login ? (
              <div>
                <button onClick={deleteMessage}>
                  <FontAwesomeIcon icon={faTrash} />
                </button>
              </div>
            ) : (
              <div>
                <button onClick={warning}>Signaler le message</button>
                <button>Suivre/Ne plus suivre</button>
              </div>
            )}
          </div>
        )}
      </div>
      <div className="content">
        <textarea
          className="texte-msg"
          rows="5"
          cols="33"
          readOnly="readonly"
          value={props.content}
        />
      </div>
      <div className="message_button">
        {!isLiked ? (
          <button onClick={like}>
            <FontAwesomeIcon icon={faHeartRegularIcon} />
          </button>
        ) : (
          <button onClick={dislike}>
            <FontAwesomeIcon icon={faHeartSolid} />
          </button>
        )}
        <button onClick={handlerSaisie}>Commenter</button>
      </div>
      <p className="date">
        {props.clock}
        {' Â· '}
        {props.date}
      </p>
      {saisir && (
        <SaisieMessage
          myLogin={props.myLogin}
          setRechargerMessages={props.setRechargerMessages}
          rechargerMessages={props.rechargerMessages}
          id={props.id}
          comment={true}
        />
      )}
    </article>
  );
}
